	/**
	 * Class Destructor
	 *
	 */
	public function __destruct ()
	{
		if (is_resource($this->oldImage))
		{
			imagedestroy($this->oldImage);
		}

		if (is_resource($this->workingImage))
		{
			imagedestroy($this->workingImage);
		}
	}

